var compactable = require('./compactable');

function populatelibs(properties, validator) {
  for (var i = properties.length - 1; i >= 0; i--) {
    var property = properties[i];
    var descriptor = compactable[property.name];

    if (descriptor && descriptor.shorthand) {
      property.shorthand = true;
      property.dirty = true;
      property.libs = descriptor.breakUp(property, compactable, validator);

      if (property.libs.length > 0)
        property.multiplex = property.libs[0].multiplex;
      else
        property.unused = true;
    }
  }
}

module.exports = populatelibs;
